$=result
for(i in $){
if(String($[i]).match(/class="title/)){
		list.push({
				text: "📖 [ "+$[i].text()+" ] 📖",
				volume: true
			})
	}
else{
		$[i].select("a").forEach(a=>{
				list.push({
						text: a.text(),
						href: a.attr("href")
					})
			})
	}
}
