	list=li.match(/.*?\n/gi)
	s=list.length-1
	e=list[s].match(/<p>.*?<\/p>/gi)[0]

	e=e.replace("<p>","")
	e=e.replace("</p>","")
	s=0
	lis=[]
	for(i=0;i<11;i++){
	if(s==0){
		s=s+1
		}else{
			e=a+c
			}
	f=java.ajax(e)
	java.setContent(f)
	c=java.getString("text.下一页.0@href||text.下一章.0@href")
	if(i!=0){
	g=java.getString("id.acontent@tag.h1@text")
	if(g!=""){
	lis[i-1]="<div>"+g+"</div><p>"+e+"</p>\n"
	}else{
		break
		}
   }
	}
	for(i=0;i<lis.length;i++){
